package building.oneblock.npc;

import building.oneblock.manager.NPCManager;
import org.bukkit.Location;
import org.bukkit.event.Listener;

import java.util.UUID;

import static building.oneblock.manager.WorldManager.SPAWN;

public class NPCCreator implements Listener {
    private NPCManager npcManager;

    public NPCCreator(NPCManager npcManager) {
        this.npcManager = npcManager;
    }

    public void createNPC() {
        npcManager.createNPC(new Location(SPAWN, 35.5, 99, -8.5, 0, 0), new NPCManager.Skin("", ""), "§eSerbius", UUID.randomUUID());

        npcManager.createNPC(new Location(SPAWN, 15.5, 99, -30.5, 90, 0), new NPCManager.Skin("", ""), "§eSerbia", UUID.randomUUID());

        npcManager.createNPC(new Location(SPAWN, -44.5, 99, -41.5, -50, 0), new NPCManager.Skin("", ""), "§cRolan", UUID.randomUUID());

        npcManager.createNPC(new Location(SPAWN, 3.5, 99, 146.5, 175, 0), new NPCManager.Skin("", ""), "§aTeya", UUID.randomUUID());
    }

}
